$(document).ready(function(){
  $('#extra_product').change(function(){
    
    var variant_id = parseInt($(this).closest('.extra_product_main').find('#extra_product_id').data('product_id'));
    var item_check = false;
    
    if($(this).is(':checked')){
      $('.extra_product_header').css('background','#C9A15B');
      $('.extra_product_inner').css('border-color','#C9A15B');
      
      jQuery.getJSON('/cart.js', function(cart) {
        for(var i = 0; i < cart.items.length; i++)
        {
          if(cart.items[i].id == variant_id && Object.keys(cart.items[i].properties) == 'Addon'){
            item_check = true;
          	break;
          }
        }
                
        if(item_check == false){
          $.ajax({
            type: 'POST',
            url: '/cart/add.js',
            data: {
              id: variant_id,
              quantity: 1,
              properties: {
                "Addon": 'true'
              }
            },  
            dataType: 'json'
          });
        }
      });
            
    }else{
      $('.extra_product_header').css('background','#000');
      $('.extra_product_inner').css('border-color','rgba(0, 0, 0, 0.2)');
      
      var line_num = 0;
      var is_item = false;
      
      jQuery.getJSON('/cart.js', function(cart) {

        for(var i = 0; i < cart.items.length; i++)
        {
          if(cart.items[i].id == variant_id && Object.keys(cart.items[i].properties) == 'Addon'){
            line_num = i + 1;
            is_item = true;
            break;
          }
        }
        
        if(is_item == true){
          $.ajax({
            type: 'POST',
            url: '/cart/change.js',
            data: 'quantity=' + 0 + '&line=' + line_num,
            dataType: 'json'
          });
        }
        
      });
      }
  });

        $('.product-page .product_bfcm_bottom_btn').click(function(){ 
          var go_to = $('.product__details').offset().top;

        $("html, body").animate({ scrollTop: go_to }, 600); 
        return false; 
      }); 



$('.product-page .product_bfcm_addtocart').click(function(){
    $('.product-page .add-to-cart__wrapper .addToCart').click();  
});

  $('.scroll_top_form').click(function(){
    $('html, body').animate({
        'scrollTop' : $(".upper_form ").position().top
    });
  });
  
  
});

